function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading ) {
		return;
   }
	if (newValue === '') {
		g_form.showFieldMsg('u_other','Briefly explain what you need.','info'); 
		return;
   }
	var st = newValue.toLowerCase();
	var cn;
	if (st == "legal1" || st== "legal 1") {
		cn = confirm('This category is available, click ok to change?');
		if (cn) {
			g_form.setValue("u_request_type","legal");
			g_form.setValue("u_what_needed","legal1");
			g_form.clearValue("u_other");
		}
	}
	else if (st == "legal2" || st== "legal 2") {
		cn = confirm('This category is available, click ok to change?');
		if (cn) {
			g_form.setValue("u_request_type","legal");
			g_form.setValue("u_what_needed","legal2");
			g_form.clearValue("u_other");
		}
	}
	else if (st == "human resources 1" || st== "hr1") {
		cn = confirm('This category is available, click ok to change?');
		if (cn) {
			g_form.setValue("u_request_type","hr");
			g_form.setValue("u_what_needed","hr1");
			g_form.clearValue("u_other");
		}
	}
	else if (st == "human resources 2" || st== "hr2") {
		cn = confirm('This category is available, click ok to change?');
		if (cn) {
			g_form.setValue("u_request_type","hr");
			g_form.setValue("u_what_needed","hr2");
			g_form.clearValue("u_other");
		}
	}
	else if (st == "facilities 1" || st== "facilities1") {
		cn = confirm('This category is available, click ok to change?');
		if (cn) {
			g_form.setValue("u_request_type","facilities");
			g_form.setValue("u_what_needed","facilities1");
			g_form.clearValue("u_other");
		}
	}
	else if (st == "facilities 2" || st== "facilities2") {
		cn = confirm('This category is available, click ok to change?');
		if (cn) {
			g_form.setValue("u_request_type","facilities");
			g_form.setValue("u_what_needed","facilities2");
			g_form.clearValue("u_other");
		}
	}
	else if (st == "cis 1" || st== "cis1") {
		cn = confirm('This category is available, click ok to change?');
		if (cn) {
			g_form.setValue("u_request_type","cis");
			g_form.setValue("u_what_needed","cis1");
			g_form.clearValue("u_other");
		}
	}
	else if (st == "cis 2" || st== "cis2") {
		cn = confirm('This category is available, click ok to change?');
		if (cn) {
			g_form.setValue("u_request_type","cis");
			g_form.setValue("u_what_needed","cis2");
			g_form.clearValue("u_other");
		}
	}
	g_form.hideFieldMsg("u_other",true);
   //Type appropriate comment here, and begin script below

}
